}

content-time.map:
function (doc) {
  var created_at = new Date(doc.created_at);
  var april15 = new Date('2018/04/15');
  if (created_at > april15) {
    emit(doc._id, {'created_at': doc.created_at, 'text': doc.text});
  }
}

near-melb-syd.map:
function (doc) {
  if (doc.coordinates != null) {
    var coords = doc.coordinates.coordinates;
    var lng = coords[0];
    var lat = coords[1];
    if (144.80 < lng && lng < 145.15 && -37.9350 < lat && lat < -37.672032) {
      emit(doc.coordinates, doc.text);
    } else if (150.9589 < lng && lng < 151.36 && -33.96 < lat && lat < -33.7633) {
      emit(doc.coordinates, doc.text);
    }
  }
}
